import { createSlice } from "@reduxjs/toolkit";
import { RecipeBuilder } from "./chunk";

export const initialState = {
  recipes : [],
  filterRecipes : [],
  searchRecipes : [],
  error: null,
  loading: false,
  currentRecipe: null
}

export const recipesSlice = createSlice({
  name: "recipes",
  initialState,
  reducers: {
    setFilterRecipes : (state, action) => {
      state.filterRecipes = action.payload === 'All' 
        ? state.recipes 
        : state.recipes.filter(recipe => recipe.tags.includes(action.payload));
    },
    setSearchRecipes : (state, action) => {
      state.searchRecipes = state.recipes.filter(recipe => recipe.title.toLowerCase().includes(action.payload.toLowerCase()))
    }
  }, // изменение состояния
  selectors: {
    getStateFilterRecipes: (state) => state.filterRecipes,
    getStateSearchRecipes: (state) => state.searchRecipes
  },
  extraReducers: RecipeBuilder, // изменения состояния в процессе выполнения асинхроного кода
});

export default recipesSlice.reducer;
export const { setFilterRecipes, setSearchRecipes } = recipesSlice.actions;
export const { getStateFilterRecipes, getStateSearchRecipes } = recipesSlice.selectors;
