import style from './Search.module.css';
import { useDispatch } from 'react-redux';
import { setFilterRecipes } from '../../Redux/Recipes';
const Search = () => {
  const dispatch = useDispatch();

  const handleSearch = () => {
    const inputValue = document.querySelector('input').value.toLocaleLowerCase();
    inputValue ? dispatch(setFilterRecipes(inputValue)) : dispatch(setFilterRecipes('All'));
  }

  return (
    <input type="search" placeholder="Search recipes" className={style.searchBar} onChange={handleSearch} />
  )
}

export default Search
