import style from './RecipeCard.module.css';
import RecipeTags from '../RecipeTags/RecipeTags';
import { Link } from 'react-router-dom';
import RecipeCardHeader from '../RecipeCardHeader/RecipeCardHeader';
const getDifficultyClassName = (difficulty) => {
  switch (difficulty) {
    case 'Easy':
      return style.easy;
    case 'Medium':
      return style.medium;
    case 'Hard':
      return style.hard;
    default:
      return style.unknown;
  }
};
const RecipeCard = ({recipe}) => {
  return (
    <div className={`${style.card} ${getDifficultyClassName(recipe.difficulty)}`}>
      <RecipeCardHeader recipe={recipe} />
      <img className={style.image} src={recipe.image} alt="" />
      <Link className= {style.link} to={`/recipe/${recipe.id}` } >View Recipe</Link>
      <div className={style.tags}>{recipe.tags.map((tag, index) => <RecipeTags key={index} tags={tag} />)}</div>
      <div className={style.difficulty}>{recipe.difficulty}</div>
    </div>
  )
}

export default RecipeCard
