import style from './Header.module.css';


const Header = () => {


  return (
    <div className={style.header}>
      <h1 className={style.title}>Recipe App</h1>
      <input type="search" placeholder="Search recipes" className={style.searchBar} />
      <button className={style.openFilter}>Filter Recipe</button> {/* сделать кнопку в компонет? так по нажатию открывается модалка с фильтрами */}
    </div>
  )
}

export default Header
