import { createAsyncThunk } from "@reduxjs/toolkit";
import { api } from "../../Api";


export const getLoginUserChunk = createAsyncThunk(
  "login/LoginUser",
  (data) => api.loginUser(data)
)

export const loginBuilder = (builder) => {
  builder.addCase(getLoginUserChunk.pending, (state) => {
    state.loading = true;
    state.error = null;
  }).addCase(getLoginUserChunk.fulfilled, (state, action) => {
    state.user = action.payload;
    state.loading = false;
  }).addCase(getLoginUserChunk.rejected, (state, action) => {
    state.error = action.error.message;
    state.loading = false;
  })
}
