import style from './Header.module.css';
import { useState } from 'react';
import FilterRecipe from '../FilterRecipe/FilterRecipe';

const Header = () => {
  const [openFilter, setOpenFilter] = useState(false);

  const handleOpenFilter = () => {
    setOpenFilter(!openFilter);
    document.querySelector('.container').classList.add('slideIn');
    document.querySelector('.container').classList.remove('slideOut');
  }

  const handleCloseFilter = () => {
    setOpenFilter(false);
    document.querySelector('.container').classList.add('slideOut');
  document.querySelector('.container').classList.remove('slideIn');
};

  return (
    <div className={style.header}>
      <h1 className={style.title}>Recipe App</h1>
      <input type="search" placeholder="Search recipes" className={style.searchBar} />
      <button className={style.openFilter} onClick={handleOpenFilter}>Filter Recipe</button>
      {openFilter && (
        <FilterRecipe onClick={handleCloseFilter} />
      )}
    </div>
  )
}

export default Header
