import style from './FilterRecipe.module.css';
import { useSelector } from 'react-redux';
import { useState } from 'react';
import RecipeTags from '../../UI/RecipeTags/RecipeTags';

export default function FilterRecipe( {onClose} ) {
  const tags = useSelector((state) => state.tag.tags);

  const handleClickOutside = (event) => {
    if (event.target.classList.contains(style.container)) {
      onClose();
    }
  };

  return (
    <div className={style.container} onClick={handleClickOutside}>
      <h1 className={style.title}>Filter Recipe</h1>
      <h2 className={style.subtitle}>Tags</h2>
      <div className={style.tags}>
        {tags.map((tag, index) => <RecipeTags key={index} tags={tag} />)}
      </div>
    </div>
  )
}
