import style from './Login.module.css';
import { useState } from 'react';
import { loginUserChunk } from '../../Redux/User/chunk';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
// import { useEffect } from 'react';
export default function Login() {

  const navigate = useNavigate();

  const [value, setValue] = useState({
    username: '',
    password: ''
  });

  const dispatch = useDispatch();

  function handleInput(e) {
    setValue({...value, [e.target.name]: e.target.value})

  }
  async function handleLogin(e) {
    e.preventDefault();
    const res = await dispatch(loginUserChunk(value));
    if(!res.payload.message) {
      navigate('/')
    } else {
      setValue({username: '', password: ''})
      document.querySelector('.error').classList.remove('hidden')
    }
  }

  return  (<div className={style.container}>
      <form action="" className={style.form} onSubmit={handleLogin}> {/*слушатель на форме для отправки данных*/}
        <h1 className={style.title}>Login</h1>
        <input className={style.input} name="username" type="text" placeholder="Enter your Name" onInput={handleInput}/>
        <input className={style.input} name="password" type="password" placeholder="Enter your Password" onInput={handleInput} />
        <input className={style.button} type="submit" value="Login"  />
        <p className="error hidden">Try again</p>
      </form>
  </div>
  )
};
