import style from './SingleRecipe.module.css';
import { useParams } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { useEffect } from 'react';
import { getSingleRecipeChunk } from '../../Redux/Recipes/chunk';
import RecipeIngredients from '../../UI/RecipeIngredients/RecipeIngredients';
import RecipeInstructions from '../../UI/RecipeInstructions/RecipeInstructions';
import RecipeTags from '../../UI/RecipeTags/RecipeTags';
export default function SingleRecipe() {
  const recipe = useSelector((state) => state.recipe.currentRecipe);
  const dispatch = useDispatch();
  const {id} = useParams();
  useEffect(() => {
    dispatch(getSingleRecipeChunk(id));
  }, [id], dispatch);

  return <div className={style.container}>
    <div className={style.card}>
      <div className={style.card__header}>
        <div className={style.cookTimeMinutes}>{recipe?.cookTimeMinutes}</div>
        <div className={style.title}>{recipe?.name}</div>
        <div className={style.rating}>{recipe?.rating}</div>
      </div>
      <img className={style.image} src={recipe?.image} alt="" />
      <div >{recipe?.ingredients.map((ingredient, index) => <RecipeIngredients key={index} ingredients={ingredient} />)}</div>
      <div >{recipe?.instructions.map((instruction, index) => <RecipeInstructions key={index} instructions={instruction} />)}</div>
      <div >{recipe?.tags.map((tag, index) => <RecipeTags key={index} tags={tag} />)}</div>
    </div>
  </div>
}
