import style from './RecipeCard.module.css';
// import RecipeIngredients from '../RecipeIngredients/RecipeIngredients';
// import RecipeInstructions from '../RecipeInstructions/RecipeInstructions';
import RecipeTags from '../RecipeTags/RecipeTags';
import { Link } from 'react-router-dom';

const getDifficultyClassName = (difficulty) => {
  switch (difficulty) {
    case 'Easy':
      return style.easy;
    case 'Medium':
      return style.medium;
    case 'Hard':
      return style.hard;
    default:
      return style.unknown;
  }
};
const RecipeCard = ({recipe}) => {
  return (
    <div className={`${style.card} ${getDifficultyClassName(recipe.difficulty)}`}>
      <div className={style.card__header}>
        <div className={style.cookTimeMinutes}>{recipe.cookTimeMinutes}</div>
        <div className={style.title}>{recipe.name}</div>
        <div className={style.rating}>{recipe.rating}</div>
      </div>
      <img className={style.image} src={recipe.image} alt="" />
      <Link className= {style.link} to={`/recipe/${recipe.id}`} >View Recipe</Link>
      <div className={style.tags}>{recipe.tags.map((tag, index) => <RecipeTags key={index} tags={tag} />)}</div>
    </div>
  )
}

export default RecipeCard
